INSERT INTO "operational"."entities" VALUES ('8d6327f2-0573-45d0-9946-deb3ba2fc62c', 'command_post_candidate', ST_GeomFromText('POINT(103.82 31.67)', 4326), '{"range": 900, "isSelect": "1", "textContent": "水西村点位建立在受灾相对较轻的村庄外围开阔地。它充分利用了现有社区的剩余设施和人文基础。本地村民熟悉情况，可以协助引导和提供信息，部分未完全倒塌的民居或公共建筑可为救援队提供遮风避雨的场所。", "locationName": "水西村"}', 'debug-system', 'system', '2025-10-30 17:35:38.796938+08', '2025-10-30 17:35:44.789317+08', NULL, 'layer.dispose', NULL, 'system', 't', 'f', '2025-10-30 17:35:38.796938+08', '{"type": "Point", "coordinates": [103.82, 31.67]}');
