DROP TABLE IF EXISTS "operational"."incident_snapshots";
CREATE TABLE "operational"."incident_snapshots" (
  "snapshot_id" uuid NOT NULL DEFAULT uuid_generate_v4(),
  "incident_id" uuid NOT NULL,
  "snapshot_type" varchar(100) COLLATE "pg_catalog"."default" NOT NULL,
  "payload" jsonb NOT NULL,
  "generated_at" timestamptz(6) NOT NULL DEFAULT now(),
  "created_by" varchar(100) COLLATE "pg_catalog"."default",
  "created_at" timestamptz(6) NOT NULL DEFAULT now()
)
;
COMMENT ON COLUMN "operational"."incident_snapshots"."snapshot_id" IS '快照主键，UUID。';
COMMENT ON COLUMN "operational"."incident_snapshots"."incident_id" IS '关联事件ID，引用 operational.events.id。';
COMMENT ON COLUMN "operational"."incident_snapshots"."snapshot_type" IS '快照类型（如 tactical_overview、resource_summary、risk_overview）。';
COMMENT ON COLUMN "operational"."incident_snapshots"."payload" IS '快照内容，JSON 结构。';
COMMENT ON COLUMN "operational"."incident_snapshots"."generated_at" IS '快照生成时间。';
COMMENT ON COLUMN "operational"."incident_snapshots"."created_by" IS '快照生成者（用户或子图）。';
COMMENT ON COLUMN "operational"."incident_snapshots"."created_at" IS '记录写入时间。';
COMMENT ON TABLE "operational"."incident_snapshots" IS '事件态势快照表，用于存储面向战略层的汇总数据。';

ALTER TABLE "operational"."incident_snapshots" ADD CONSTRAINT "incident_snapshots_pkey" PRIMARY KEY ("snapshot_id");
ALTER TABLE "operational"."incident_snapshots" ADD CONSTRAINT "incident_snapshots_incident_fkey" FOREIGN KEY ("incident_id") REFERENCES "operational"."events" ("id") ON DELETE CASCADE;
CREATE INDEX "idx_incident_snapshots_incident" ON "operational"."incident_snapshots" USING btree ("incident_id");
CREATE INDEX "idx_incident_snapshots_type" ON "operational"."incident_snapshots" USING btree ("snapshot_type");
CREATE INDEX "idx_incident_snapshots_generated" ON "operational"."incident_snapshots" USING btree ("generated_at");

