-- ----------------------------
-- Table structure for scheme
-- ----------------------------
DROP TABLE IF EXISTS "operational"."scheme";
CREATE TABLE "operational"."scheme" (
  "id" uuid NOT NULL,
  "plan_data" text COLLATE "pg_catalog"."default" NOT NULL,
  "generated_by" varchar(50) COLLATE "pg_catalog"."default" NOT NULL,
  "generated_at" timestamptz(6) NOT NULL DEFAULT now(),
  "confirmed_by" varchar(100) COLLATE "pg_catalog"."default",
  "confirmed_at" timestamptz(6),
  "dispatched_at" timestamptz(6),
  "created_at" timestamptz(6) NOT NULL DEFAULT now(),
  "updated_at" timestamptz(6) NOT NULL DEFAULT now(),
  "scheme_type" int4 NOT NULL,
  "status" int4 NOT NULL DEFAULT 0,
  "event_id" uuid NOT NULL,
  "hazard_type" int4
)
;
COMMENT ON COLUMN "operational"."scheme"."id" IS '方案ID。';
COMMENT ON COLUMN "operational"."scheme"."plan_data" IS '方案文本描述（Markdown 或纯文本），供指挥席阅读。';
COMMENT ON COLUMN "operational"."scheme"."generated_by" IS '方案生成方式：ai=AI生成，manual=人工录入。';
COMMENT ON COLUMN "operational"."scheme"."generated_at" IS '方案生成时间。';
COMMENT ON COLUMN "operational"."scheme"."confirmed_by" IS '确认方案的指挥员或角色。';
COMMENT ON COLUMN "operational"."scheme"."confirmed_at" IS '方案确认时间。';
COMMENT ON COLUMN "operational"."scheme"."dispatched_at" IS '方案下发时间，下发后席位进入准备流程。';
COMMENT ON COLUMN "operational"."scheme"."created_at" IS '记录创建时间。';
COMMENT ON COLUMN "operational"."scheme"."updated_at" IS '记录最近更新时间。';
COMMENT ON COLUMN "operational"."scheme"."scheme_type" IS '方案类型 0：侦察方案 1：救援方案';
COMMENT ON COLUMN "operational"."scheme"."status" IS '方案状态 0：未完成 1：已完成';
COMMENT ON COLUMN "operational"."scheme"."event_id" IS '事件uuid';
COMMENT ON COLUMN "operational"."scheme"."hazard_type" IS '告警灾害类型 1是地震 2是洪涝灾害 3是山体滑坡/泥石流 4是台风 5森林火灾 6危化品泄露 7油罐车爆炸 8社会安全事件 9突发传染病疫情';
COMMENT ON TABLE "operational"."scheme" IS '智能方案表，存储AI生成或人工创建的智能方案。';

