-- ----------------------------
-- Table structure for task_route_plans
-- ----------------------------
DROP TABLE IF EXISTS "operational"."task_route_plans";
CREATE TABLE "operational"."task_route_plans" (
  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),
  "task_id" uuid NOT NULL,
  "status" varchar(16) COLLATE "pg_catalog"."default" NOT NULL,
  "strategy" varchar(32) COLLATE "pg_catalog"."default",
  "origin_geojson" jsonb,
  "destination_geojson" jsonb,
  "polyline_geojson" jsonb,
  "distance_meters" float8,
  "duration_seconds" int4,
  "estimated_arrival_time" timestamptz(6),
  "avoid_polygons" jsonb,
  "created_at" timestamptz(6) NOT NULL DEFAULT now(),
  "updated_at" timestamptz(6) NOT NULL DEFAULT now()
)
;
COMMENT ON COLUMN "operational"."task_route_plans"."id" IS '主键ID，UUID，唯一标识一条路线规划记录。';
COMMENT ON COLUMN "operational"."task_route_plans"."task_id" IS '关联的任务ID，对应 operational.tasks.id，用于区分不同任务。';
COMMENT ON COLUMN "operational"."task_route_plans"."status" IS '路线使用状态：USED=当前使用路线，UNUSED=备选路线。';
COMMENT ON COLUMN "operational"."task_route_plans"."strategy" IS '路线规划策略（如FASTEST/SHORTEST），与高德API的strategy参数一致。';
COMMENT ON COLUMN "operational"."task_route_plans"."origin_geojson" IS '路线起点GeoJSON，通常为救援队伍/设备实时位置。';
COMMENT ON COLUMN "operational"."task_route_plans"."destination_geojson" IS '路线终点GeoJSON，通常为任务目标点位。';
COMMENT ON COLUMN "operational"."task_route_plans"."polyline_geojson" IS '路线轨迹GeoJSON LineString，供前端渲染整条路径。';
COMMENT ON COLUMN "operational"."task_route_plans"."distance_meters" IS '路线总里程，单位：米。';
COMMENT ON COLUMN "operational"."task_route_plans"."duration_seconds" IS '预计行驶时长，单位：秒。';
COMMENT ON COLUMN "operational"."task_route_plans"."estimated_arrival_time" IS '预计到达时间（ETA），基于生成时刻与时长推算。';
COMMENT ON COLUMN "operational"."task_route_plans"."avoid_polygons" IS '避障区域集合（JSONB），存放调用时提交的多边形集合。';
COMMENT ON COLUMN "operational"."task_route_plans"."created_at" IS '记录创建时间，即路线生成时间。';
COMMENT ON COLUMN "operational"."task_route_plans"."updated_at" IS '记录最后更新时间，由触发器自动维护。';
COMMENT ON TABLE "operational"."task_route_plans" IS '任务路径规划表：记录某任务生成的多条候选路线及其当前使用状态。';

