-- ----------------------------
-- Table structure for tasks
-- ----------------------------
DROP TABLE IF EXISTS "operational"."tasks";
CREATE TABLE "operational"."tasks" (
  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),
  "type" "operational"."task_type_enum" NOT NULL,
  "status" "operational"."task_status_enum" NOT NULL DEFAULT 'pending'::operational.task_status_enum,
  "priority" int2 NOT NULL DEFAULT 50,
  "description" text COLLATE "pg_catalog"."default",
  "deadline" timestamptz(6),
  "result" jsonb,
  "progress" int2 DEFAULT 0,
  "target_entity_id" uuid,
  "created_by" varchar(100) COLLATE "pg_catalog"."default",
  "updated_by" varchar(100) COLLATE "pg_catalog"."default",
  "created_at" timestamptz(6) NOT NULL DEFAULT now(),
  "updated_at" timestamptz(6) NOT NULL DEFAULT now(),
  "deleted_at" timestamptz(6),
  "scheme_id" uuid,
  "event_id" uuid,
  "code" varchar(255) COLLATE "pg_catalog"."default"
)
;
COMMENT ON COLUMN "operational"."tasks"."id" IS '子任务主键，UUID。';
COMMENT ON COLUMN "operational"."tasks"."type" IS '任务类型：uav_recon=无人机侦察，set_perimeter=设立警戒线，rescue_target=解救目标，material_transport=物资运输，damage_assessment=损毁评估。';
COMMENT ON COLUMN "operational"."tasks"."status" IS '任务状态：pending=待执行，in_progress=执行中，completed=已完成，failed=失败。';
COMMENT ON COLUMN "operational"."tasks"."priority" IS '任务优先级，0-100，越大表示越优先。';
COMMENT ON COLUMN "operational"."tasks"."description" IS '任务描述或行动要求。';
COMMENT ON COLUMN "operational"."tasks"."deadline" IS '任务截止时间。';
COMMENT ON COLUMN "operational"."tasks"."result" IS '任务执行结果JSON，记录产出或反馈。';
COMMENT ON COLUMN "operational"."tasks"."progress" IS '任务完成度百分比（0-100）。';
COMMENT ON COLUMN "operational"."tasks"."target_entity_id" IS '任务关注的目标实体ID，引用 operational.entities.id。';
COMMENT ON COLUMN "operational"."tasks"."created_by" IS '创建任务的用户或Agent。';
COMMENT ON COLUMN "operational"."tasks"."updated_by" IS '最近更新任务的用户或Agent。';
COMMENT ON COLUMN "operational"."tasks"."created_at" IS '任务创建时间。';
COMMENT ON COLUMN "operational"."tasks"."updated_at" IS '任务最近更新时间。';
COMMENT ON COLUMN "operational"."tasks"."deleted_at" IS '软删除时间。';
COMMENT ON COLUMN "operational"."tasks"."scheme_id" IS '方案id';
COMMENT ON COLUMN "operational"."tasks"."event_id" IS '关联事件ID，引用 operational.events.id，可为空用于调试场景';
COMMENT ON COLUMN "operational"."tasks"."code" IS '任务编号';
COMMENT ON TABLE "operational"."tasks" IS '子任务表，将任务拆分为可执行的指令单元。';

