-- ----------------------------
-- Function structure for activate_navigation_route
-- ----------------------------
DROP FUNCTION IF EXISTS "operational"."activate_navigation_route"("p_route_id" uuid);
CREATE FUNCTION "operational"."activate_navigation_route"("p_route_id" uuid)
  RETURNS "pg_catalog"."bool" AS $BODY$
DECLARE
    v_mission_id UUID;
BEGIN
    -- 获取路线所属的任务ID
    SELECT mission_id INTO v_mission_id
    FROM operational.navigation_routes
    WHERE id = p_route_id;

    IF v_mission_id IS NULL THEN
        RETURN FALSE;
    END IF;

    -- 停用该任务的所有其他路线
    UPDATE operational.navigation_routes
    SET is_active = FALSE,
        deactivated_at = NOW()
    WHERE mission_id = v_mission_id
      AND is_active = TRUE
      AND id != p_route_id;

    -- 激活新路线
    UPDATE operational.navigation_routes
    SET is_active = TRUE,
        activated_at = NOW()
    WHERE id = p_route_id;

    -- 更新任务的当前路线ID
    UPDATE operational.missions
    SET current_route_id = p_route_id
    WHERE id = v_mission_id;

    RETURN TRUE;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
COMMENT ON FUNCTION "operational"."activate_navigation_route"("p_route_id" uuid) IS '激活导航路线。自动停用同任务的其他路线，并更新任务的current_route_id字段。';

-- ----------------------------
-- Function structure for check_task_dependency_cycle
-- ----------------------------
DROP FUNCTION IF EXISTS "operational"."check_task_dependency_cycle"("p_child_task_id" uuid, "p_parent_task_id" uuid);
CREATE FUNCTION "operational"."check_task_dependency_cycle"("p_child_task_id" uuid, "p_parent_task_id" uuid)
  RETURNS "pg_catalog"."bool" AS $BODY$
DECLARE
    has_cycle boolean;
BEGIN
    -- 使用递归CTE检测循环
    WITH RECURSIVE task_chain AS (
        -- 初始查询: 从待添加的父任务开始
        SELECT parent_task_id, child_task_id, 1 as depth
        FROM operational.task_dependencies
        WHERE child_task_id = p_parent_task_id

        UNION ALL

        -- 递归查询: 沿依赖链向上追溯
        SELECT d.parent_task_id, d.child_task_id, tc.depth + 1
        FROM operational.task_dependencies d
        INNER JOIN task_chain tc ON d.child_task_id = tc.parent_task_id
        WHERE tc.depth < 100  -- 防止无限递归
    )
    SELECT EXISTS (
        SELECT 1 FROM task_chain
        WHERE parent_task_id = p_child_task_id
    ) INTO has_cycle;

    RETURN has_cycle;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
COMMENT ON FUNCTION "operational"."check_task_dependency_cycle"("p_child_task_id" uuid, "p_parent_task_id" uuid) IS '检测添加新依赖关系是否会导致循环依赖,返回true表示存在循环';

-- ----------------------------
-- Function structure for prevent_task_dependency_cycle
-- ----------------------------
DROP FUNCTION IF EXISTS "operational"."prevent_task_dependency_cycle"();
CREATE FUNCTION "operational"."prevent_task_dependency_cycle"()
  RETURNS "pg_catalog"."trigger" AS $BODY$
BEGIN
    IF operational.check_task_dependency_cycle(NEW.child_task_id, NEW.parent_task_id) THEN
        RAISE EXCEPTION '检测到循环依赖: 任务 % 和 % 形成依赖环', NEW.child_task_id, NEW.parent_task_id;
    END IF;
    RETURN NEW;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

