-- ----------------------------
-- Function structure for validate_equipment_list
-- ----------------------------
DROP FUNCTION IF EXISTS "operational"."validate_equipment_list"("p_equipment_list" jsonb);
CREATE FUNCTION "operational"."validate_equipment_list"("p_equipment_list" jsonb)
  RETURNS "pg_catalog"."bool" AS $BODY$
DECLARE
    item jsonb;
    required_fields text[] := ARRAY['name', 'quantity', 'priority'];
    field text;
BEGIN
    -- 空数组是有效的
    IF jsonb_array_length(p_equipment_list) = 0 THEN
        RETURN true;
    END IF;

    -- 遍历每个装备项
    FOR item IN SELECT jsonb_array_elements(p_equipment_list)
    LOOP
        -- 检查必需字段
        FOREACH field IN ARRAY required_fields
        LOOP
            IF NOT (item ? field) THEN
                RAISE EXCEPTION '装备项缺少必需字段: %', field;
            END IF;
        END LOOP;

        -- 检查quantity是正整数
        IF (item->>'quantity')::int <= 0 THEN
            RAISE EXCEPTION '装备数量必须大于0';
        END IF;

        -- 检查priority是有效值
        IF NOT (item->>'priority' IN ('critical', 'recommended', 'optional')) THEN
            RAISE EXCEPTION '装备优先级必须是critical/recommended/optional之一';
        END IF;
    END LOOP;

    RETURN true;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
COMMENT ON FUNCTION "operational"."validate_equipment_list"("p_equipment_list" jsonb) IS '验证装备清单JSON格式,确保每项包含name/quantity/priority字段且值合法';

-- ----------------------------
-- View structure for active_hazard_zones
-- ----------------------------
DROP VIEW IF EXISTS "operational"."active_hazard_zones";
CREATE VIEW "operational"."active_hazard_zones" AS  SELECT zone_id,
    zone_name,
    hazard_type,
    severity,
    st_asgeojson(area::geometry)::json AS area_geojson,
    valid_from,
    valid_until,
        CASE
            WHEN valid_until IS NULL THEN '永久有效'::text
            ELSE concat('剩余', EXTRACT(hour FROM valid_until - now()), '小时')
        END AS validity_status,
    properties
   FROM operational.hazard_zones
  WHERE deleted_at IS NULL AND (valid_until IS NULL OR valid_until > now())
  ORDER BY severity DESC, valid_from DESC;
COMMENT ON VIEW "operational"."active_hazard_zones" IS '活跃危险区域视图。仅显示当前有效的危险区，并提供GeoJSON格式的区域数据供前端地图渲染使用。';

-- ----------------------------
-- View structure for poi_statistics
-- ----------------------------
DROP VIEW IF EXISTS "operational"."poi_statistics";
CREATE VIEW "operational"."poi_statistics" AS  SELECT poi_type,
    count(*) AS total_count,
    count(*) FILTER (WHERE area_sqm >= 1000::double precision) AS large_sites_count,
    avg(area_sqm) AS avg_area_sqm,
    avg(slope) AS avg_slope,
    min(elevation) AS min_elevation,
    max(elevation) AS max_elevation
   FROM operational.poi_points
  WHERE deleted_at IS NULL
  GROUP BY poi_type
  ORDER BY (count(*)) DESC;
COMMENT ON VIEW "operational"."poi_statistics" IS 'POI统计视图。按类型汇总POI数量、平均面积、平均坡度等统计信息，用于系统监控和数据质量检查。';

-- ----------------------------
-- View structure for available_rescuers
-- ----------------------------
DROP VIEW IF EXISTS "operational"."available_rescuers";
CREATE VIEW "operational"."available_rescuers" AS  SELECT rescuer_id,
    name,
    rescuer_type,
    team_name,
    st_astext(current_location::geometry) AS location_wkt,
    status,
    skills,
    phone,
    radio_frequency
   FROM operational.rescuers
  WHERE availability = true AND status::text = 'available'::text AND deleted_at IS NULL
  ORDER BY rescuer_type, name;
COMMENT ON VIEW "operational"."available_rescuers" IS '可用救援资源视图：筛选出当前可调度的人员和车辆';

-- ----------------------------
-- View structure for high_vulnerability_areas
-- ----------------------------
DROP VIEW IF EXISTS "operational"."high_vulnerability_areas";
CREATE VIEW "operational"."high_vulnerability_areas" AS  SELECT factor_id,
    factor_name,
    factor_type,
    st_astext(location::geometry) AS location_wkt,
    population_count,
    vulnerability_score,
    importance_level,
    infrastructure_type
   FROM operational.vulnerability_factors
  WHERE vulnerability_score >= 0.7 AND status::text = 'active'::text AND deleted_at IS NULL
  ORDER BY vulnerability_score DESC, importance_level DESC;
COMMENT ON VIEW "operational"."high_vulnerability_areas" IS '高脆弱性区域视图：vulnerability_score >= 0.7';

